set define off verify off feedback off
  whenever sqlerror exit sql.sqlcode rollback
  DECLARE
     l_application_id      NUMBER := 400;
     l_workspace_id        NUMBER;
     l_workspace_name      VARCHAR2 (100) := 'DRBLANKET';
     l_parsing_schema      VARCHAR2 (100);
     l_application_alias   VARCHAR2 (100) := 'BESTILLINGSBLANKET-TIL-DANTAXI';
     l_authentication_scheme VARCHAR2 (100);
  BEGIN
      select case sys_context('USERENV', 'DB_NAME') when 'APX1TST' then 'Application Express Accounts' when 'APX1PRD' then 'Application Express Accounts' else null end
      into l_authentication_scheme from dual;
      select aa.workspace_id, ws.schema 
      into l_workspace_id, l_parsing_schema 
  from apex_applications aa, apex_workspace_schemas ws where ws.workspace_name = aa.workspace and aa.workspace=l_workspace_name fetch first 1 rows only;
     apex_application_install.set_workspace_id (l_workspace_id);
     apex_application_install.set_application_id (l_application_id);
     apex_application_install.generate_offset;
     apex_application_install.set_schema (l_parsing_schema);
     apex_application_install.set_application_alias (NVL (l_application_alias, 'F' || apex_application_install.get_application_id));
      if l_authentication_scheme is not null then      
        apex_application_install.set_authentication_scheme(p_name =>l_authentication_scheme);
     end if; END;
